INSERT INTO `vf_posts` VALUES (243, 1, '2017-04-27 09:55:09', '2017-04-27 02:55:09', '<p align=\"center\">BẠN NHẬN NHÀ THÔ, BẠN QUÁ BẬN RỘN, BẠN CẦN MỘT ĐƠN VỊ UY TÍN?</p>\r\n&nbsp;\r\n\r\nQuý khách khi nhận nhà thô là biệt thự, nhà phố, căn hộ, chỉ cần cung cấp mặt bằng, Nhà Xinh sẽ làm việc để đưa ra những ý tưởng thiết kế và cung cấp trọn gói cho không gian, từ tủ bếp, phòng khách, phòng ăn, phòng ngủ, phòng làm việc… và những món đồ trang trí làm cho không gian thêm sống động.\r\n\r\n&nbsp;\r\n<ul type=\"none\">\r\n 	<li style=\"list-style-type: none;\">\r\n<ul type=\"none\">\r\n 	<li><img src=\"http://www.nhaxinh.com/image/check.png\" /> Cung cấp ý tưởng thiết kế cho ngôi nhà</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n&nbsp;\r\n<ul type=\"none\">\r\n 	<li style=\"list-style-type: none;\">\r\n<ul type=\"none\">\r\n 	<li><img src=\"http://www.nhaxinh.com/image/check.png\" /> Thiết kế cải tạo mặt bằng</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n&nbsp;\r\n<ul type=\"none\">\r\n 	<li style=\"list-style-type: none;\">\r\n<ul type=\"none\">\r\n 	<li><img src=\"http://www.nhaxinh.com/image/check.png\" /> Giám sát, thi công hoàn thiện mặt bằng</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n&nbsp;\r\n<ul type=\"none\">\r\n 	<li style=\"list-style-type: none;\">\r\n<ul type=\"none\">\r\n 	<li><img src=\"http://www.nhaxinh.com/image/check.png\" /> Cung cấp nội thất trọn gói</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n&nbsp;\r\n<ul type=\"none\">\r\n 	<li><img src=\"http://www.nhaxinh.com/image/check.png\" /> Dịch vụ “chìa khóa trao tay”</li>\r\n</ul>', 'TƯ VẤN THIẾT KẾ NỘI THẤT', '', 'publish', 'open', 'open', '', 'tu-van-thiet-ke-noi', '', '', '2017-04-27 09:58:01', '2017-04-27 02:58:01', '', 0, 'http://luxury.fonicweb.com/?p=243', 0, 'post', '', 0);
